function initAddFixedClass() {
	var win = jQuery(window);
	var fixedClassWrap = 'fixed-header';
	var fixedClassHeader = 'fixed-position';
	var wrapper = jQuery('#wrapper');
	var header = jQuery('#header');

	function onClass() {
		if (win.scrollTop() > 10) {
			wrapper.addClass(fixedClassWrap);
			header.addClass(fixedClassHeader);
		} else {
			wrapper.removeClass(fixedClassWrap);
			header.removeClass(fixedClassHeader);
		}
	}

	onClass();
	win.on('scroll resize orientationchange', onClass);
}

